
var HelpModule = function() {
  var self = this;

  self.helpListener = function(from, to, message) {
    if(message == '!help') {
      
      this.notice(from, 'I am owned by theSpiked. Please contact him for suggestions, corrections and/or feedback.');

      this.notice(from, 'My commands are:');
      //this.notice(from, '!summon <username> [message]');
      //this.notice(from, '  Summon <username> by sending them a pushmessage. (In private message only, use with caution)');
      this.notice(from, '!dogevalue <amount>');
      this.notice(from, '  Shows the value of <amount> doge in BTC and USD.');
      this.notice(from, '!balance <Dogecoin address>');
      this.notice(from, '  Shows the balance of the <Dogecoin address>');
      this.notice(from, '!ping');
      this.notice(from, '  Makes me say "Pong!"');
    }
  };

  /**
   * Unloads all listeners
  */
  self.unload = function(client) {
    client.removeListener('message', self.helpListener);
  };

  /**
   * Loads all listeners
  */
  self.load = function(client) {
    client.addListener('message', self.helpListener);
  };

};

module.exports = new HelpModule();
