var LocalLogger = function() {
  var self = this;

  self.messageLogger = function(from, to, message) {
    if(to == this.opt.nick) {
      var timestamp = (new Date()).toUTCString();

      console.log('[' + timestamp + '] <' + from + '> ' + message);
    }
  };

  self.noticeLogger = function(from, to, message) {
    var timestamp = (new Date()).toUTCString();
    console.log('[' + timestamp + '] NOTICE <' + from + '> ' + message);
  };


  /**
   * Unloads all listeners
  */
  self.unload = function(client) {
    client.removeListener('message', self.messageLogger);

    client.removeListener('notice', self.noticeLogger);

  };

  /**
   * Loads all listeners
  */
  self.load = function(client) {
    client.addListener('message', self.messageLogger);

    client.addListener('notice', self.noticeLogger);
  };

};

module.exports = new LocalLogger();
