var DogeChainModule = function() {
  var self = this;

  self.pingPongListener = function(from, to, message) {
    if(message == '!ping') {
      if(to == this.opt.nick) {
        to = from;
      }
      
      this.say(to, 'Pong!');
    }
  };


  /**
   * Unloads all listeners
  */
  self.unload = function(client) {
    client.removeListener('message', self.pingPongListener);
  };

  /**
   * Loads all listeners
  */
  self.load = function(client) {
    client.addListener('message', self.pingPongListener);
  };

};

module.exports = new DogeChainModule();
